mapper.select:
SELECT COUNT(uid)
FROM selection_info
WHERE cid = #{id}

SELECT cid, uid
FROM selection_info
WHERE cid = #{cid}
  AND uid = #{uid}

SELECT cid, uid
FROM teaching_info
WHERE cid = #{cid}
  AND uid = #{uid}

SELECT cid
FROM selection_info
WHERE uid = #{uid}

SELECT cid
FROM teaching_info
WHERE uid = #{uid}

SELECT live
FROM class_info
WHERE id = #{id}

mapper.update:
UPDATE class_info
SET live=#{status}
WHERE id = #{id}
